mapper.select:
SELECT concat(a.kd_rek_1, a.kd_rek_2, a.kd_rek_3, a.kd_rek_4, a.kd_rek_5) kode_akun, a.kd_rek_1, a.kd_rek_2, a.kd_rek_3, a.kd_rek_4, a.kd_rek_5, nm_rek_1, nm_rek_2, nm_rek_3, nm_rek_4, nm_rek_5, COALESCE(a.realisasi,0) sebelum_eliminasi, (COALESCE(a.realisasi,0) - COALESCE((select COALESCE(b.realisasi,0) from compilation_record5 b inner join elimination_account c on b.tahun = c.tahun and b.kd_pemda = c.kd_pemda and b.kd_rek_1 = c.kd_rek_1 and b.kd_rek_2 = c.kd_rek_2 and b.kd_rek_3 = c.kd_rek_3 and b.kd_rek_4 = c.kd_rek_4 and b.kd_rek_5 = c.kd_rek_5
a.tahun = b.tahun and a.kd_pemda = b.kd_pemda and a.kd_rek_1 = b.kd_rek_1 and a.kd_rek_2 = b.kd_rek_2 and a.kd_rek_3 = b.kd_rek_3 and a.kd_rek_4 = b.kd_rek_4 and a.kd_rek_5 = b.kd_rek_5 and a.kd_provinsi = b.kd_provinsi and a.kd_satker=b.kd_satker and a.periode_id = b.periode_id and a.perubahan_id = b.perubahan_id
), 0)) setelah_eliminasi FROM compilation_record5 a
a.tahun = #{tahunAnggaran} AND a.periode_id = #{periodeId}
AND a.kd_pemda = #{othersParam.value_cb}
AND a.kd_pemda in (select concat(a.kdprovinsi,'.', a.kdpemda) from ref_pemda2 a where kdprovinsi = #{othersParam.value_cb})
AND a.kd_pemda in (select concat(a.kdprovinsi,'.', a.kdpemda) from ref_pemda2 a where kwilayah = to_number(#{othersParam.value_cb},'999'))
order by a.kd_rek_1, a.kd_rek_2, a.kd_rek_3, a.kd_rek_4, a.kd_rek_5 asc

select.if:
select 
concat(a.kdprovinsi,'.', a.kdpemda) value_cb, 
concat(a.kdprovinsi,'.', a.kdpemda, ' ', a.urpemda) label_cb 
from ref_pemda2 a			
			
	a.tahun = #{tahunAnggaran}
	and  a.kdpemda != '00'  
	
		and a.kdprovinsi = #{othersParam.kdprovinsi}
	
	
		and a.kwilayah = #{othersParam.kwilayah}

select 
distinct a.kdprovinsi value_cb, concat(a.kdprovinsi,' ', a.urpemda) label_cb 
from ref_pemda2 a 

	a.kdpemda = '00' and a.tahun = #{tahunAnggaran} 
	
		and a.kwilayah = #{othersParam.kwilayah}

select distinct to_char(a.kwilayah, '99') value_cb, concat(a.kwilayah, ' ',b.nama_wilayah) label_cb from ref_pemda2 a inner join ref_wilayah b on a.kwilayah = b.id
a.tahun = #{tahunAnggaran}
order by 1 asc
