mapper.select:
select id, kd_user, auth_key, email, username, status, pemda_id from
econsole.user

	
		username like #{params.username}
	

order by ${orderBy} ${direction}
limit #{pageSize} offset (#{pageSize}
* #{pageSequence})

mapper.update:
update econsole.user set kd_user = #{params.kd_user},
auth_key = #{params.auth_key}, email = #{params.email},
username = #{params.username}, status = #{params.status}, pemda_id =
#{params.pemda_id}
where id = #{params.id}
